svg.script:
const svg = document.documentElement;
let timer;
svg.addEventListener('mouseenter', () => {
  timer = setTimeout(() => {
    document.getElementById('animWave').beginElement();
  }, 1000); // smooth 1 second delay
});
svg.addEventListener('mouseleave', () => {
  clearTimeout(timer);
  document.getElementById('animWave').endElement && document.getElementById('animWave').endElement();
});
svg.addEventListener('touchstart', () => {
  timer = setTimeout(() => {
    document.getElementById('animWave').beginElement();
  }, 1000);
});
svg.addEventListener('touchend', () => {
  clearTimeout(timer);
});
